// src/components/Locations.js
import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Locations.css'; // Ensure CSS is linked

// --- Constants ---
const IMAGE_PATH = '/images/';
const BEAR_NORMAL_SRC = IMAGE_PATH + 'bear.png';
const BEAR_SCARY_SRC = IMAGE_PATH + 'bear(1).png';
const TREE_SRC = IMAGE_PATH + 'tree_PNG2517.png'; // Use correct filename in public/images
const AXE_SRC = IMAGE_PATH + 'axe.png';
const WOOD_PILE_SRC = IMAGE_PATH + 'wood.png';
const FLINT_SRC = IMAGE_PATH + 'stone.png'; // Use correct filename in public/images
const CAMPFIRE_SRC = IMAGE_PATH + 'bonfire.png';
// Background path is set in CSS now

const MAX_BEAR_SIZE = 250; // Pixels - Triggers scare
const INITIAL_BEAR_SIZE = 50;
const BEAR_GROWTH_RATE = 1.2; // Faster rate
const BEAR_GROWTH_INTERVAL = 100;
const CAMPFIRE_SHRINK_AMOUNT = 50; // Not currently used in logic, but kept constant
const TREE_HEALTH = 6; // Number of hits to chop
// const LOGS_PER_TREE = 5; // (Not used visually in this version)
// const LOGS_FOR_FIRE = 4; // (Not used visually in this version)
// const FLINT_STRIKES_NEEDED = 3; // (Not used in logic currently)
const CUT_TREE_INTERVAL = 400; // ms between hover cuts

// --- Date Restriction Constants ---
const BASIC_MIN_DAYS_AHEAD = 3; // Basic users must book at least 3 days in advance
const PREMIUM_MIN_DAYS_AHEAD = 1; // Premium users can book from tomorrow
const MAX_DAYS_AHEAD = 90; // Max booking window for everyone

// --- Helper ---
const getRandom = (min, max) => Math.random() * (max - min) + min;

// Function to format date as YYYY-MM-DD
const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are 0-indexed
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};
// --- End Helper ---

function Locations() {
  // --- State ---
  const location = useLocation();
  const navigate = useNavigate();
  const { airline, flights, user } = location.state || {}; // User object now includes company status
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [originsList, setOriginsList] = useState([]);
  const [destinationsList, setDestinationsList] = useState([]);

  // --- NEW: Date State ---
  const [selectedDate, setSelectedDate] = useState('');
  const [dateError, setDateError] = useState(''); // For validation errors
  // --- End NEW ---

  // --- Game State ---
  const [bear, setBear] = useState({ size: INITIAL_BEAR_SIZE, src: BEAR_NORMAL_SRC, isFading: false });
  const [tree, setTree] = useState({ health: TREE_HEALTH, chopped: false, isHit: false });
  const [woodPile, setWoodPile] = useState({ exists: false, isLit: false });
  const [activeTool, setActiveTool] = useState(null); // 'axe', 'flint', or null
  const [canCutTree, setCanCutTree] = useState(true); // For cut throttling
  const [isReloading, setIsReloading] = useState(false); // Prevent render during reload

  // Refs
  const containerRef = useRef(null);
  const bearIntervalRef = useRef(null);
  const treeHitTimeoutRef = useRef(null);
  const cutThrottleTimeoutRef = useRef(null);

  // --- Get Company Status ---
  const companyStatus = user?.company?.status || 'basic'; // Default to 'basic' if missing

  // --- Calculate Date Restrictions using useMemo ---
  const { minBookingDate, maxBookingDate } = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Normalize to start of day

    const minDays = companyStatus === 'premium' ? PREMIUM_MIN_DAYS_AHEAD : BASIC_MIN_DAYS_AHEAD;

    const minDate = new Date(today);
    minDate.setDate(today.getDate() + minDays);

    const maxDate = new Date(today);
    maxDate.setDate(today.getDate() + MAX_DAYS_AHEAD);

    return {
      minBookingDate: formatDate(minDate),
      maxBookingDate: formatDate(maxDate),
    };
  }, [companyStatus]); // Recalculate only if companyStatus changes

  // --- Effects ---
  useEffect(() => { // Initialize Dropdowns
    if (flights && Array.isArray(flights)) {
      setOriginsList([...new Set(flights.map(f => f.origin))]);
      setDestinationsList([...new Set(flights.map(f => f.destination))]);
    } else {
      console.warn("Locations: Flights data missing or not an array.", flights);
      setOriginsList([]);
      setDestinationsList([]);
    }
  }, [flights]);

  useEffect(() => { // Bear Growing Logic
    const startBearGrowth = () => {
      if (bearIntervalRef.current) clearInterval(bearIntervalRef.current);
      bearIntervalRef.current = setInterval(() => {
        setBear((prevBear) => {
          // Check if bear should stop growing
          if (prevBear.isFading || prevBear.src === BEAR_SCARY_SRC || woodPile.isLit) {
            if (bearIntervalRef.current) clearInterval(bearIntervalRef.current);
            return prevBear; // No change
          }
          // Calculate new size
          const newSize = prevBear.size + BEAR_GROWTH_RATE;
          // Check if max size reached
          if (newSize >= MAX_BEAR_SIZE) {
            if (bearIntervalRef.current) clearInterval(bearIntervalRef.current);
            console.log("BEAR SCARED! Setting state to scary.");
            return { ...prevBear, size: MAX_BEAR_SIZE, src: BEAR_SCARY_SRC, isFading: false };
          }
          // Grow the bear
          return { ...prevBear, size: newSize };
        });
      }, BEAR_GROWTH_INTERVAL);
    };

    // Start growth only under specific conditions
    if (!woodPile.isLit && !bear.isFading && bear.src !== BEAR_SCARY_SRC) {
      startBearGrowth();
    } else {
      // Ensure interval is cleared if conditions aren't met
      if (bearIntervalRef.current) clearInterval(bearIntervalRef.current);
    }

    // Cleanup interval on unmount or when dependencies change
    return () => { if (bearIntervalRef.current) clearInterval(bearIntervalRef.current); };
  }, [woodPile.isLit, bear.isFading, bear.src]); // Dependencies ensure effect reruns correctly

  useEffect(() => { // Reload on Scare (with Increased Delay)
    let timer;
    if (bear.src === BEAR_SCARY_SRC && !isReloading) {
      console.log("Scary bear state detected, starting reload timer...");
      setIsReloading(true); // Prevent further renders/actions
      timer = setTimeout(() => {
        alert("The bear got too close!");
        window.location.reload();
      }, 1500); // 1.5 seconds delay
    }
    // Cleanup timeout if component unmounts or state changes before timeout completes
    return () => clearTimeout(timer);
  }, [bear.src, isReloading]); // Dependencies

  useEffect(() => { // Cleanup other timeouts on unmount
    return () => {
      if (treeHitTimeoutRef.current) clearTimeout(treeHitTimeoutRef.current);
      if (cutThrottleTimeoutRef.current) clearTimeout(cutThrottleTimeoutRef.current);
    };
  }, []); // Empty dependency array means this runs only on unmount

  // --- Callbacks ---
  const cutTreeAction = useCallback(() => {
    if (tree.chopped || !canCutTree) return; // Check throttle and chopped status

    setCanCutTree(false); // Throttle starts
    setTree(prev => ({ ...prev, isHit: true })); // Trigger animation state

    // Clear previous hit timeout if any
    if (treeHitTimeoutRef.current) clearTimeout(treeHitTimeoutRef.current);
    // Set timeout to remove hit state after animation duration
    treeHitTimeoutRef.current = setTimeout(() => setTree(prev => ({ ...prev, isHit: false })), 300);

    // Update Tree Health & State
    setTree(prevTree => {
      const newHealth = Math.max(0, prevTree.health - 1);
      console.log("Tree health:", newHealth);
      if (newHealth <= 0 && !prevTree.chopped) {
        console.log("Tree chopped down!");
        setWoodPile(prevPile => ({ ...prevPile, exists: true, isLit: false })); // Wood pile appears
        setActiveTool(null); // Deactivate axe after chopping
        return { ...prevTree, health: 0, chopped: true }; // Mark as chopped
      }
      return { ...prevTree, health: newHealth }; // Just update health
    });

    // Set timeout to allow cutting again after interval
    cutThrottleTimeoutRef.current = setTimeout(() => {
      setCanCutTree(true);
    }, CUT_TREE_INTERVAL);

  }, [tree.chopped, tree.health, canCutTree]); // Include all state dependencies used

  const handleToolClick = useCallback((toolType, event) => {
    event.stopPropagation(); // Prevent background click from also firing
    setActiveTool(prevTool => (prevTool === toolType ? null : toolType)); // Toggle tool selection
  }, []); // No dependencies needed

  const handleTreeClick = useCallback((event) => {
    event.stopPropagation(); // Prevent background click
    // Allow direct click to cut if axe is active, tree isn't chopped, has health, and not throttled
    if (activeTool === 'axe' && !tree.chopped && tree.health > 0 && canCutTree) {
      console.log("Direct tree click with axe");
      cutTreeAction(); // Perform the cut action
    }
  }, [activeTool, tree.chopped, tree.health, canCutTree, cutTreeAction]); // Dependencies

  const handleWoodPileClick = useCallback((event) => {
    event.stopPropagation(); // Prevent background click
    // Allow lighting if flint is active, wood exists, and it's not already lit
    if (activeTool === 'flint' && woodPile.exists && !woodPile.isLit) {
      console.log("Lighting fire with flint");
      setWoodPile(prev => ({ ...prev, isLit: true })); // Light it
      // Stop bear growth and start fading the bear away
      if (bearIntervalRef.current) clearInterval(bearIntervalRef.current);
      setBear(prev => ({ ...prev, isFading: true }));
      setActiveTool(null); // Deactivate flint after use
    }
  }, [activeTool, woodPile.exists, woodPile.isLit]); // Dependencies

  const handleBackgroundClick = useCallback(() => {
    // Deactivate any active tool if clicked on the background
    if (activeTool) {
      console.log("Deactivating tool via background click");
      setActiveTool(null);
    }
  }, [activeTool]); // Dependency

  // --- NEW: Date Change Handler ---
  const handleDateChange = (e) => {
    setSelectedDate(e.target.value);
    setDateError(''); // Clear any previous date error when user changes the date
  };
  // --- End NEW ---

  // --- MODIFIED UI Logic ---
  const handleProceed = () => {
    setDateError(''); // Clear previous validation errors

    // 1. Check if date is selected
    if (!selectedDate) {
        setDateError('Please select a travel date.');
        return; // Don't proceed
    }

    // 2. Validate Weekend Booking for Basic Users
    if (companyStatus === 'basic') {
        // Create Date object carefully, specifying time to avoid timezone issues with just date string
        const dateObj = new Date(selectedDate + 'T00:00:00');
        const dayOfWeek = dateObj.getDay(); // 0 = Sunday, 6 = Saturday
        if (dayOfWeek === 0 || dayOfWeek === 6) {
            setDateError('Weekend bookings are only available for premium companies. Please select a weekday.');
            return; // Don't proceed
        }
    }

    // 3. Find matching flights (existing logic - assumes flights are for any date for now)
    // IMPORTANT: In a real scenario, you'd likely filter flights based on the selectedDate here too!
    const matchingFlights = flights?.filter(f => f.origin === origin && f.destination === destination) || [];

    // 4. Navigate if valid date and route selected
    if (matchingFlights.length > 0) {
      console.log(`Proceeding to seats for ${airline} ${origin}-${destination} on ${selectedDate}`);
      // Pass selectedDate to the next route (/seats)
      navigate('/seats', {
          state: {
              airline,
              origin,
              destination,
              flights: matchingFlights, // Potentially pre-filtered by date if API supported it
              user, // Includes company status
              selectedDate // Pass the chosen date
          }
      });
    } else {
      // If no flights match origin/destination (date filtering might happen later)
      alert("No flights available for this specific route.");
    }
  };

  const handleBack = () => navigate(-1); // Go back to the previous page

  // Determine if proceed button should be disabled based on selections
  const proceedDisabled = !origin || !destination || !selectedDate;

  // --- Render ---
  // Prevent rendering if reload is imminent (due to bear scare)
  if (isReloading) {
    return (
        <div className="locations-page-wrapper reloading-notice">
            <img src={BEAR_SCARY_SRC} alt="Scared!" style={{width: `${MAX_BEAR_SIZE}px`, position: 'absolute', bottom: '2%', right: '5%', transform: 'translateX(50%)', zIndex: 10}}/>
            <p>The bear got too close... reloading!</p>
        </div>
     );
  }

  // Early return if essential user data is missing
   if (!user || !user.company) {
     return (
         <div className="locations-page-wrapper error-notice">
             <p>Error: User or Company data not loaded correctly.</p>
             <button onClick={() => navigate('/')} className="locations-button go-home">Go Home</button>
         </div>
     );
   }

  // Main component render
  return (
    <div
      className={`locations-page-wrapper ${activeTool ? `tool-active-${activeTool}` : ''}`}
      ref={containerRef}
      onClick={handleBackgroundClick} // Handle clicks on the background to deactivate tools
    >
      {/* --- Camping Elements Container --- */}
      <div className="camping-elements-container">
        {/* Bear Image - Source changes based on state */}
        <img
          src={bear.src}
          alt="Bear"
          className={`bear ${bear.isFading ? 'fading' : ''}`} // Apply fading class if needed
          style={{ width: `${bear.size}px` }} // Dynamic size
        />

        {/* Tree Container - Only render if not chopped */}
        {!tree.chopped && (
          <div
            className={`tree-container ${activeTool === 'axe' ? 'choppable' : ''} ${tree.isHit ? 'hit' : ''}`} // Classes for interaction feedback
            onClick={handleTreeClick} // Click action handler
          >
            {/* Tree Image - Opacity reflects health */}
            <img src={TREE_SRC} alt="Tree" className="tree-image" style={{ opacity: tree.health > 0 ? 0.4 + (tree.health / TREE_HEALTH) * 0.6 : 0.4 }}/>
            {/* Tree Health Bar - Show if health is below max */}
            {tree.health < TREE_HEALTH && tree.health > 0 && ( // Also hide if health is 0
                <div className="tree-health-bar-container">
                  <div className="tree-health-bar-inner" style={{ width: `${(tree.health / TREE_HEALTH) * 100}%` }}></div>
                </div>
            )}
          </div>
        )}

        {/* Wood Pile Image - Show if tree is chopped, hide if lit */}
        {woodPile.exists && !woodPile.isLit && (
          <img
            src={WOOD_PILE_SRC}
            alt="Wood Pile"
            className={`wood-pile ${activeTool === 'flint' ? 'lightable' : ''}`} // Interaction class
            onClick={handleWoodPileClick} // Click action handler
          />
        )}

        {/* Campfire Image - Show if wood pile is lit */}
        {woodPile.isLit && (
          <img src={CAMPFIRE_SRC} alt="Campfire" className="campfire lit" />
        )}

        {/* Tools Area */}
        <div className="tools-area">
             {/* Axe Tool */}
             <img src={AXE_SRC} alt="Axe" className={`tool axe ${activeTool === 'axe' ? 'active' : ''}`} onClick={(e) => handleToolClick('axe', e)} />
             {/* Flint Tool */}
             <img src={FLINT_SRC} alt="Flint" className={`tool flint ${activeTool === 'flint' ? 'active' : ''}`} onClick={(e) => handleToolClick('flint', e)} />
         </div>
      </div>
      {/* --- End Camping Elements --- */}


      {/* --- UI Overlay --- */}
      <div className="locations-ui-overlay">
         {/* Display Company Status */}
         <p className="company-status-indicator">
            Company Plan: <span className={companyStatus}>{companyStatus.toUpperCase()}</span>
         </p>
         {/* Main Heading */}
         <h3>Select Route from <strong>{airline || 'Selected Airline'}</strong></h3>
         {/* Back Button */}
         <button onClick={handleBack} className="locations-button back">ðŸ”™ Change Airline</button>

         {/* Group for Location and Date Selectors */}
         <div className="location-selectors-group">
             {/* Location Selectors Row */}
             <div className="location-selectors">
                {/* Origin Selector */}
                <div className="select-group">
                  <label htmlFor="origin-select">Origin:</label>
                  <select id="origin-select" value={origin} onChange={(e) => setOrigin(e.target.value)} required>
                    <option value="">-- Select Origin --</option>
                    {originsList.map((loc) => (<option key={`orig-${loc}`} value={loc}>{loc}</option>))}
                  </select>
                </div>
                {/* Destination Selector */}
                <div className="select-group">
                  <label htmlFor="dest-select">Destination:</label>
                  <select id="dest-select" value={destination} onChange={(e) => setDestination(e.target.value)} required>
                    <option value="">-- Select Destination --</option>
                    {destinationsList.map((loc) => (<option key={`dest-${loc}`} value={loc}>{loc}</option>))}
                  </select>
                </div>
             </div>

             {/* Date Selector Group */}
              <div className="select-group date-selector">
                 <label htmlFor="date-select">Travel Date:</label>
                 <input
                    type="date"
                    id="date-select"
                    value={selectedDate}
                    onChange={handleDateChange}
                    min={minBookingDate} // Set minimum bookable date
                    max={maxBookingDate} // Set maximum bookable date
                    required // HTML5 validation attribute
                 />
                 {/* Show weekend restriction info only for basic users */}
                 {companyStatus === 'basic' && (
                    <small className="date-restriction-info">Weekends unavailable for Basic plan.</small>
                 )}
                 {/* Display validation error message if any */}
                 {dateError && <p className="error-message date-error">{dateError}</p>}
              </div>
             {/* --- End Date Selector --- */}
         </div>
         {/* --- End Group --- */}

         {/* Proceed Button */}
         <button
            onClick={handleProceed}
            disabled={proceedDisabled} // Disable if origin, dest, or date is missing
            className="locations-button proceed"
         >
           Continue to Seat Selection â†’
         </button>
         {/* Optional: Show informational message when button is disabled but no error exists */}
         {proceedDisabled && !dateError && <p className="info-message">Please select origin, destination, and date.</p>}
      </div>
      {/* --- End UI Overlay --- */}
    </div>
  );
}

export default Locations;
